mapper.select:
select lx.id lxid,lx.lxmc lxmc,pp.id ppid,pp.ppmc ppmc,xh.id xhid,xh.xhmc xhmc, lb.id , lb.lbmc, lb.ysdj, lb.kcl,lb.fz, lb.createtime, lb.zt, lb.bz,lb.fz from qysswj_xxzx_t_yw_lxwh lx left join qysswj_xxzx_t_yw_ppwh pp on lx.id=pp.lxid left join qysswj_xxzx_t_yw_xhwh xh on pp.id=xh.ppid left join qysswj_xxzx_t_yw_lbwh lb on xh.id=lb.xhid
lx.zt='1' and pp.zt='1' and xh.zt='1' and lb.zt='1'
and lx.id=#{lxid}
and pp.id=#{ppid}
and xh.id=#{xhid}
and lb.kcl < #{kcl}
and lb.lbmc like '%${lbmc}%'
order by lb.createtime desc

select count(*) from qysswj_xxzx_t_yw_lxwh lx left join qysswj_xxzx_t_yw_ppwh pp on lx.id=pp.lxid left join qysswj_xxzx_t_yw_xhwh xh on pp.id=xh.ppid left join qysswj_xxzx_t_yw_lbwh lb on xh.id=lb.xhid
lx.zt='1' and pp.zt='1' and xh.zt='1' and lb.zt='1'
and lx.id=#{lxid}
and pp.id=#{ppid}
and xh.id=#{xhid}
and lb.kcl < #{kcl}
and lb.lbmc like '%${lbmc}%'

select lx.id lxid,lx.lxmc lxmc,pp.id ppid,pp.ppmc ppmc,xh.id xhid,xh.xhmc xhmc, lb.id , lb.lbmc, lb.ysdj, lb.kcl, lb.createtime, lb.zt, lb.bz,lb.fz, 0 cgsl from qysswj_xxzx_t_yw_lxwh lx left join qysswj_xxzx_t_yw_ppwh pp on lx.id=pp.lxid left join qysswj_xxzx_t_yw_xhwh xh on pp.id=xh.ppid left join qysswj_xxzx_t_yw_lbwh lb on xh.id=lb.xhid
lx.zt='1' and pp.zt='1' and xh.zt='1' and lb.zt='1'
and lb.id=#{id}
